function bl_change_background_mode(mode){
	switch(mode){
		case 'image':
			jQuery('.background_image').removeClass('hide');
			jQuery('.background_pattern, .background_color').addClass('hide');
		break;
		case 'pattern':
			jQuery('.background_pattern').removeClass('hide');
			jQuery('.background_color, .background_image').addClass('hide');
		break;
		case 'color':
			jQuery('.background_color').removeClass('hide');
			jQuery('.background_pattern, .background_image').addClass('hide');
		break;
	}	
}

function bl_change_header_background_mode(mode){
	switch(mode){
		case 'color':
			jQuery('.header_background_image').addClass('hide');
		break;
		case 'image':
			jQuery('.header_background_image').removeClass('hide');
		break;
	}	
}
